class Solution {
public:

    // Check result >= lo
    template <class T, class U> T lt(T lo, T hi, U f) {
        --lo, assert(lo <= hi);
        while (lo < hi) {
            T mid = lo + (hi - lo + 1) / 2;
            f(mid) ? lo = mid : hi = mid - 1;
        }
        return lo;
    }

    int lim = -1;

    long long calcmaxmin(int s, long long have, vector<int>&flowers, int target, int n) {
        if(lim >= s || s == n) return 0;

        long long r = lt(1ll, (long long)(target - 1), [&](long long g) -> bool {
            long long want = 0;
            for (int i = s; i < n; i++) {
                want += max(0LL, g - flowers[i]);
            }
            return want <= have;
        });
        // dbg(r, s, have);
        return (s < n ? r : 0);
    }
    
    long long maximumBeauty(vector<int>& flowers, long long newFlowers, int target, int full, int partial) {
        sort(flowers.rbegin(), flowers.rend());
        int n = (int)flowers.size();

        for (int i = 0; i < n; i++) if(flowers[i] >= target) lim = i;

        long long need = 0;
        long long res = partial * calcmaxmin(0, newFlowers, flowers, target, n);

        for (int i = 0; i < n; i++) {
            need += max(0, target - flowers[i]);
            // dbg(need, res);
            if(need <= newFlowers) {
                res = max(res, (i + 1) * full + partial * calcmaxmin(i + 1, newFlowers - need, flowers, target, n));
            } else break;
        }
        return res;

    }
};


